import React, {useState} from 'react';

import {
    Form,
    Input
} from './styled'

const SearchBarComponent = () => {
    const [term, setTerm] = useState('');

    const handleOnChange = (e) => {
        setTerm(e.target.value)
    }

    return (
        <Form>
            <Input 
                type="text"
                onChange={handleOnChange}
                placeholder='Try me'
                value={term}
            />
            <Button type='submit'>
            </Button>
        </Form>
    )
}

export default SearchBarComponent;
