import React, { useState, useEffect } from 'react'
import unsplash from '../api/unsplash';

import BackgroundImages from '../components/BackgroundImage/BackgroundImages'
import SearchBar from '../components/BackgroundImage/SearchBarComponent'
import Loader from '../components/LoaderComponent'

const HomePage = () => {
  const [loading, setLoading] = useState(true);
  const [images, setImages] = useState([]);
  const [term, setTerm] = useState('montains');

  useEffect(() => {
    getData()
  }, []);

  const getData = async () => {
    setLoading(true);

    await unsplash.get('/search/photos', {
        params: { query: term },
    }).then(result => {
        setImages(result.data.results);
        setLoading(false);
    });
}
    return (
      <>
        { loading && <Loader /> }

        <BackgroundImages 
          images={images}
        />
        <SearchBar 
        />
      </>

    )
}

export default HomePage;
