import styled, {keyframes} from 'styled-components'
import variables from '../../constants/styleVariables';


const SliderAnimation = keyframes`
    0% {
        transform: translate(-50%, -50%) scale(1);
    }
    100% {
        transform: translate(-50%, -50%) scale(1.25);
    }
`;

export const Container = styled.div`
    top: 0;
    left: 0;
    position: absolute;
    width: 100%;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-content: center;
    z-index: 1;
`;

export const BackgroundContainer = styled.div`
    width: 50%;
    height: 50%;
    z-index: 2;
    position: absolute;
    overflow: hidden;
    top: 0;
    left: 0;

    &:nth-of-type(2) {
        top: 0;
        right: 0;
        left: auto;
        transform: scale(-1, 1);
    }

    &:nth-of-type(3) {
        top: auto;
        bottom: 0;
        left: 0;
        transform: scale(1, -1);
    }

    &:nth-of-type(4) {
        top: auto;
        left: auto;
        bottom: 0;
        right: 0;
        transform: scale(-1, -1);
    }
`;

export const Image = styled.img`
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    position: absolute;
    min-height: 100%;
    min-width: 100%;
    animation: ${SliderAnimation} 10s linear infinite alternate;
`;

export const Overlay = styled.div`
    background: #000;
    opacity: 0.2;
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    left: 0;
    z-index: 3;
`;


export const Warning = styled.h5`
    z-index: ${variables.zIndex.content};
    color: ${variables.colors.white};
    text-align: center;
    box-shadow: 2px 2px 2px rgba(0,0,0,0.15)l
`;
