import React, {useState, useEffect} from 'react'
import unsplash from '../../api/unsplash';


import {
    Container,
    BackgroundContainer,
    Image,
    Overlay
} from './styled'

const BackgroundImages = () => {
    const [images, setImages] = useState(false);
    const [term, setTerm] = useState('poland');

    useEffect(() => {
        getData()
    }, []);

    const getData = async () => {
        await unsplash.get('/search/photos', {
            params: { query: term },
        }).then(result => {
            setImages(result.data.results);
        });

    }

    const renderDiv = (src, alt) => {

        let item = [];
        for (let i = 0; i < 4; i++) {
          item.push(
            <BackgroundContainer 
              key={ i }
            >
                <Image 
                    src={src} 
                    alt={alt}
                />
            </BackgroundContainer> 
          );
        }
        return item;
    };

    const randomNumber = () => (
        Math.floor(Math.random() * 10)
    )

    return (
        <Container>
            {/* {images && images.map((image, index) => (
                <BackgroundContainer 
                key={ index}
                >
                    <Image 
                        src={image.urls.regular}
                        alt={image.alt_description}
                    />
                </BackgroundContainer> 

                ))
            } */}

            {images && 
                renderDiv(
                    images[randomNumber()].urls.regular, 
                    images[randomNumber()].alt_description
                )
            }
            <Overlay />
        </Container>
    )
}


export default BackgroundImages;
