import React, {useState, useEffect} from 'react'
import Source from '../../assets/slide-1.jpg'
import unsplash from '../../api/unsplash';


import {
    Container,
    BackgroundContainer,
    Image,
    Overlay
} from './styled'

const BackgroundImages = () => {
    const [images, setImages] = useState([]);

    useEffect(() => {
        getData()
    });

    const getData = async () => {
        const response = await unsplash.get('/photos/random');
        setImages(response.data);
    }

    const renderDiv = (extraClass) => {
        let item = [];
        for (let i = 0; i < 4; i++) {
          item.push(
            <BackgroundContainer 
              key={ i }
            >
                <Image src={Source}/>
            </BackgroundContainer> 
          );
        }
        return item;
    };

    return (
        <Container>
            {renderDiv()}
            <Overlay />
        </Container>
    )
}


export default BackgroundImages;
