import React, {useState, useEffect} from 'react'
import unsplash from '../../api/unsplash';
import defaultSlide from '../../assets/default-slide.jpg'

import {
    Container,
    BackgroundContainer,
    Image,
    Overlay
} from './styled'

const BackgroundImages = () => {
    const [images, setImages] = useState([]);
    const [loading, setLoading] = useState(false);
    const [term, setTerm] = useState('sea');

    useEffect(() => {
        getData()
    }, []);

    const getData = async () => {
        await unsplash.get('/search/photos', {
            params: { query: term },
        }).then(result => {
            setImages(result.data.results);
        });

    }

    const renderDiv = (src, alt) => {

        let item = [];
        for (let i = 0; i < 4; i++) {
          item.push(
            <BackgroundContainer 
              key={ i }
            >
                <Image 
                    src={src} 
                    alt={alt}
                />
            </BackgroundContainer> 
          );
        }
        return item;
    };

    const randomNumber = () => (
        Math.floor(Math.random() * 10)
    )

    return (
        <Container>
            {images && images[9] !== undefined ? ( 
                renderDiv(
                    images[randomNumber()].urls.regular, 
                    images[randomNumber()].alt_description
                )) : ((
                    renderDiv(
                        defaultSlide,
                        'underground image'
                    )
                ))
            }
            <Overlay />
        </Container>
    )
}


export default BackgroundImages;
