import React, {useRef} from 'react';
import * as PIXI from 'pixi.js'
import pic from '../assets/default-slide.jpg'
import { Stage, Sprite } from '@inlet/react-pixi';


const Bunny = () => {

    const canvas = useRef(null);


    if(canvas) {
        const stageView = new PIXI.Application({
            view: canvas,
            width: 300, 
            height: 300
        });

        const texture = PIXI.Texture.from('sprite.png');
        const img = new PIXI.Sprite(texture);

        img.x = stageView.renderer.width / 2;
        img.y = stageView.renderer.height / 2;
        img.anchor.x = 0.5;
        img.anchor.y = 0.5;

        stageView.stage.addChild(img);
    }

    return (
        <Stage>
            <Sprite image={pic} x={Stage.width / 2} y={100} />
        </Stage>
    )
}

export default Bunny;
