import React, {useRef, useEffect} from 'react'
import * as PIXI from "pixi.js";
import Bg from '../assets/grunge.jpg'
import LoaderPic from '../assets/dis-varOne.png'


const Bunny = () => {
    const myCanvas = useRef(null);

    useEffect(() => {
        setScene()
    })

    const setScene = () => {

        var picture = new PIXI.Application(500, 480, {
            autoStart: false, 
            backgroundColor: 0x000000, 
            view: myCanvas
          });

        myCanvas.appendChild(picture.view);

        const bg = PIXI.Sprite.from(Bg);

        bg.anchor.set(0.5);

        bg.x = picture.screen.width / 2;
        bg.y = picture.screen.height / 2;

        picture.stage.addChild(bg);

        picture.ticker.add(() => {
            bg.rotation += 0.1;
        });
    }

    return (
        <div>
            <canvas id='myCanvas' width='500' height='500' ref={myCanvas}/>
            <img src={Bg}/>
        </div>
    )
};

export default Bunny;
