import styled, {keyframes} from 'styled-components'

const SliderAnimation = keyframes`
    0% {
        transform: translate(-50%, -50%) scale(1);
    }
    100% {
        transform:  translate(-50%, -50%) scale(1.5);
    }
`;

export const Container = styled.div`
    top: 0;
    left: 0;
    position: absolute;
    width: 100%;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-content: center;
    z-index: 1;
`;

export const BackgroundContainer = styled.div`
    width: 50%;
    height: 50%;
    z-index: 2;
    position: absolute;
    overflow: hidden;
    top: 0;
    left: 0;

    &:nth-of-type(2) {
        top: 0;
        right: 0;
        left: auto;
        transform: scale(-1, 1);
    }

    &:nth-of-type(3) {
        top: auto;
        bottom: 0;
        left: 0;
        transform: scale(1, -1);
    }

    &:nth-of-type(4) {
        top: auto;
        left: auto;
        bottom: 0;
        right: 0;
        transform: scale(-1, -1);
    }
`;

export const Image = styled.img`
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    position: absolute;
    min-height: 100%;
    min-width: 100%;
    animation: ${SliderAnimation} 35s linear infinite alternate;
`;
