import styled from 'styled-components'

export const Container = styled.div`
    top: 0;
    left: 0;
    position: absolute;
    width: 100%;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-content: center;
    z-index: 1;
`;

export const BackgroundContainer = styled.div`
    width: 50%;
    height: 50%;
    z-index: 2;
    position: absolute;
    overflow: hidden;
    top: 0;
    left: 0;

    &::nth-of-type(2) {
        top: 0;
        right: 0;
        left: auto;
        transform: scale(-1, 1);
    }

    &::nth-of-type(1) {
        top: auto;
        bottom: 0;
        left: 0;
        transform: scale(1, -1);
    }
`;
