import React, {useReducer, useRef} from 'react'
import { Stage, Sprite, useTick } from '@inlet/react-pixi'

import pic from '../assets/default-slide.jpg'

import BackgroundLinesComponent from '../components/BackgroundLinesComponent/BackgroundLinesComponent';

import {
  Container,
  Paragraph,
  CopyContainer,
  MainTitle
} from '../components/commonStyles'

const AboutPage = () => {

  const reducer = (_, { data }) => data
  const Bunny = () => {
    const [motion, update] = useReducer(reducer)
    const iter = useRef(0)

    useTick(delta => {
      const i = (iter.current += 0.05 * delta)

      update({
        type: 'update',
        data: {
          x: Math.sin(i) * 100,
          y: Math.sin(i / 1.5) * 100,
          rotation: Math.sin(i) * Math.PI,
          anchor: Math.sin(i / 2),
        },
      })
    })

    return (
      <Sprite
        image="https://s3-us-west-2.amazonaws.com/s.cdpn.io/693612/IaUrttj.png"
        {...motion}
      />
    )
  }

  return (
    <Container>
      <BackgroundLinesComponent />
      <MainTitle>
        Co tam KrÄ™cisz?
      </MainTitle>

      <CopyContainer>
        <Paragraph>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor 
          incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud 
          exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
        </Paragraph>

        <Stage width={300} height={300} options={{ backgroundColor: 0x1d2230 }}>
          <Container x={150} y={150}>
            <Bunny />
          </Container>
        </Stage>
      </CopyContainer>
    </Container>

  )
}

export default AboutPage;
