const CounterComponent = () => {
  const [numer, serNumber] = useState(1);

  return (
    <>
      <p>Count: {numer}</p>
      <button onClick={() => serNumber(numer + 1)}>Increment</button>
      <button onClick={() => serNumber(numer - 1)}>Decrement</button>
    </>
  );
};

