import React, {useState, useEffect} from 'react'
import Source from '../../assets/slide-1.jpg'
import unsplash from '../../api/unsplash';


import {
    Container,
    BackgroundContainer,
    Image,
    Overlay
} from './styled'

const BackgroundImages = () => {
    const [images, setImages] = useState([]);
    const [term, setTerm] = useState('sea');

    useEffect(() => {
        getData();
    },[]);

    const getData = async () => {
        await unsplash.get('/search/photos', {
            params: { query: term },
        }).then(result => {
            setImages(result.data.results);
            console.log(result)
        });

    }

    const renderDiv = (source, alt) => {
        let item = [];
        for (let i = 0; i < 4; i++) {
          item.push(
            <BackgroundContainer 
              key={ i }
            >
                <Image 
                    src={source}
                    alt={alt}
                />
            </BackgroundContainer> 
          );
        }
        return item;
    };

    return (
        <Container>
            {/* {images && images.map((image, index) => (
                <BackgroundContainer 
                key={ index}
                >
                    <Image 
                        src={image.urls}
                        alt={image.alt_description}
                    />
                </BackgroundContainer> 

                ))
            } */}
            <Overlay />
        </Container>
    )
}


export default BackgroundImages;
